mapper.select:
SELECT c.CAR_ID carId, c.LICENSEPLATE licenseplate, c.technicalgrade technicalgrade, c.LICENSEPLATECOLOR licenseplatecolor, c.MODELS models, c.PASSENGERS passengers, c.LICENSENUMBER licensenumber, c.TRANSPORTPERMITS transportpermits, c.COMPANY company, c.CREATEDATE createdate, c.FREEZE freeze, c.OPER_ID operId, c.OPER_TIME operTime, sr.ID securityrecordId, IFNULL(sr.checkstatus, 0) checkstatus, case when sr.checkstatus = 0 then '待安检' when sr.checkstatus = -1 then '未通过' when sr.checkstatus IS NULL then '待安检' when sr.checkstatus = 1 then '已通过' END checkstatusName, inspector,sr.checkdatetime checkdatetime FROM wt_cars c LEFT JOIN wt_securityrecord sr ON sr.licenseplate = c.licenseplate and sr.CHECKDATETIME = (select MAX(sr1.CHECKDATETIME) from wt_securityrecord sr1 where sr1.licenseplate = sr.licenseplate) WHERE c.freeze = ${NOFREEZE}
AND c.COMPANY = #{company,jdbcType=VARCHAR}
AND c.licenseplate = #{licenseplate,jdbcType=VARCHAR}
AND sr.checkstatus = #{checkstatus}
Order by c.Oper_Time desc
