mapper.select:
    select  u.USER_ID userID, u.USER_NAME username, u.USER_PASSWORD userPassword, r.role_name roleName,r.role_id roleId
    from user u,user_to_role ur,role r,wt_userinfo ui
    where u.user_id = ur.user_id and ur.role_id = r.role_id and u.USER_ID = ui.USER_ID
		and r.ROLE_NAME != #{roleName,jdbcType=VARCHAR}
		and ui.COMPANY = #{companyId,jdbcType=VARCHAR}
